import React, { useState } from "react";
import '../Css/login.css';
import img5 from '../image/login1.png';
import {NavLink , useHistory} from 'react-router-dom';
import {FaEnvelope} from "react-icons/fa";
import { RiLockPasswordFill} from "react-icons/ri";


const Login = () => {

  const history = useHistory();
  const [ email, setEMIL] = useState('');
  const  [password, setPassw] = useState('');
    

  const Handler = (e) => {
    setEMIL(e.target.value)
  }

  const Handler1 = (e) => {
    setPassw(e.target.value)

  }

  const  OPenData = async (e) => {
  
    const respo = await fetch('/login',{
      method: "POST",
      headers: {
        "Content-Type" : "application/json"
      },
      body:JSON.stringify({
        email:email,
        password:password
      })
    
    });
    const info = respo.json();
    if(respo.status === 422 || !info){
      alert("invalid");
      
    }else{
      alert("successfully login");
      history.push("/");
    }

  }



    return (
      <>
       <div className="container-fluid  mt-5 text-center">
         <div className="LoginCon col-lg-8 col-md-8 mx-auto">
          <div className="row ">
                   <div className="col-md-6">
                     <div className="leftDiv">
                       <form method="POST" className="Loginform">
                       <h1>Login form</h1>
                       <div className="Inputfield">
                                 <FaEnvelope/>
                                 <input  type="email" className="input-Box" name="email" placeholder="Your Email" id="email" value={email}
                                 onChange={Handler}
                                 required="true"/>
                                 </div>
                                 <div className="inputfield">
                                 <RiLockPasswordFill/>
                                 <input  type="password" className="input-Box" name="password" autoComplete="off" placeholder="Your Password" id="password"  value={password}
                              onChange={Handler1}
                               required="true"/>
                                 </div>
                                 <input type="submit" name="Submit" id="Submit" value="Login" className="loginbtn" 
                                 onClick={OPenData}/>
                                 <p><NavLink to="/Signup" className="Box">Create a Account</NavLink></p>
                       </form>
                         </div>
                    </div>
                    <div className="col-md-6 d-flex">
                               <div className="RightDiv">
                                 <img src={img5}  className="img-fluid img-thumbnail"alt='loginpic'/>
                               </div>
                      </div>
                </div>
         </div>
       </div>

 </>
    );
  }
  
  export default Login;
