import React, { useState } from "react";
import img5 from '../image/signnup1.jpg';
import {NavLink, useHistory} from 'react-router-dom';
import {FaUserAlt,FaEnvelope,FaPhoneAlt} from "react-icons/fa";
import { MdOutlineWork} from "react-icons/md";
import { RiLockPasswordFill} from "react-icons/ri";

   const Signup = () => {
    const [get, setGEt] = useState({
      name:"" ,
      email:"",
      phone:"",
      work:"",
      password:"",
      cpassword:""
    });

    return (
      <>
           
          <div className="container cont1 mx-auto  ">
               <div className="Signup-cont col-lg-10 mx-auto">
                   <div className="row">
                         <div className="col-md-6">
                            <div className="LeftCon">
                               <form  method="POST" className="SignupForm  text-center">
                                 <h1>SignUp</h1>
                                 <div className="text-group">
                                 <FaUserAlt/>
                                 <input  type="text" className="text-Box" name="name" placeholder="Your Name" id="name"  value={get.name}
                                   onChange={myHandler}/>
                                 </div>

                                 <div className="text-group">
                                 <FaEnvelope/>
                                 <input  type="email" className="text-Box" name="email" placeholder="Your Email" id="email" 
                                  value={get.email}
                                  onChange={myHandler}/>
                                 </div>

                                 <div className="text-group">
                                 <FaPhoneAlt/>
                                 <input  type="number" className="text-Box" name="Phone" placeholder="Phone no" id="Phone"  value={get.phone}
                                 onChange={myHandler}
                                 required="true"/>
                                 </div>
                                 <div className="text-group">
                                 <MdOutlineWork/>
                                 <input  type="text" className="text-Box" name="work" placeholder="Your Profession" id="work"  value={get.work}
                                 onChange={myHandler} 
                                 required="true"/>
                                 </div>

                                 <div className="text-group">
                                 <RiLockPasswordFill/>
                                 <input  type="password" className="text-Box" name="password" placeholder="Your Password" id="password" 
                                 value={get.password}
                                  onChange={myHandler}
                                required="true"/>
                                 </div>

                                 <div className="text-group">
                                 <RiLockPasswordFill/>
                                 <input  type="password" className="text-Box" name="cpassword" placeholder="Confirm Password" id="cpassword"
                                 value={get.cpassword}
                                 onChange={myHandler} 
                                required="true"/>
                                 </div>
                                 

                                 <input type="submit" name="Submit" id="Submit" value="Register" className="stylebtn" />
                                 </form> 
                                 <p >  Already Register<NavLink to="/login" className="header">Login </NavLink></p>

                            </div>
                         </div>

                         <div className="col-md-6">
                               <div className="RightCon">
                                 <img src={img5}  className="img-fluid img-thumbnail"alt='signuppic'/>
                               </div>

                         </div>
                   </div>
               </div>
          </div>
            
     
      </>

    );
  }
  export default Signup;
